import signBuilder from '@dashlane/pqc-sign-dilithium5-node'
import assert from 'node:assert/strict'

async function run() {
    const sign = await signBuilder();
    
    const message = new Uint8Array([0x44, 0x61, 0x73, 0x68, 0x6c, 0x61, 0x6e, 0x65]); // 
    // generated to be tested at https://dashlane.github.io/pqc.js
    const { publicKey, privateKey } = await sign.keypair();
    const { signature } = await sign.sign(message, privateKey);
    const validSignature = await sign.verify(signature, message, publicKey);

    console.log('locally generated, signed and verified signature');
    console.log('pk');
    console.log(Buffer.from(publicKey).toString('base64'));
    console.log('message');
    console.log(Buffer.from(message).toString('base64'));
    console.log('signature');
    console.log(Buffer.from(signature).toString('base64'));
    console.log('sk');
    console.log(Buffer.from(privateKey).toString('base64'));
    console.log('Verified signature:', validSignature); 
    console.log("Unexpected: using the public key and the signature, the message failed the verification in the playground");
    assert(validSignature === true);

    console.log('Locally generated signature is valid');
    // generated by https://dashlane.github.io/pqc.js
    
    // previous example generated by https://dashlane.github.io/pqc.js
    // const pGMessage = 'base'
    // const pGpub = ''
    // const pGpriv = ''
    // const pGsign=''
    
    // example generated in the Playground, verify the signature fine in the playground fail in the code below
    const pGMessage = 'RGFzaGxhbmU='
    const pGpub = ''
    const pGpriv = ''
    const pGsign=''

    console.log("pGpub")
    console.log(pGpub)
    console.log("pGmessage")
    console.log(pGMessage)
    console.log("pGsign")
    console.log(pGsign)
    console.log("pGpriv")
    console.log(pGpriv)


    // const pGpubUint8Array = new Uint8Array(Buffer.from(pGpub, 'base64'));
    // const pGprivUint8Array = new Uint8Array(Buffer.from(pGpriv, 'base64'));
    // const pGsignUint8Array = new Uint8Array(Buffer.from(pGsign, 'base64'));
    // const pGmessageUint8Array = new Uint8Array(Buffer.from(pGMessage, 'base64'));
    const pGpubUint8Array = Uint8Array.from(atob(pGpub), char => char.charCodeAt(0));
    const pGprivUint8Array = Uint8Array.from(atob(pGpriv), char => char.charCodeAt(0));
    const pGsignUint8Array = Uint8Array.from(atob(pGsign), char => char.charCodeAt(0));
    const pGmessageUint8Array = Uint8Array.from(atob(pGMessage), char => char.charCodeAt(0));

    const webVerified = await sign.verify(
        pGsignUint8Array,
        pGmessageUint8Array, 
        pGpubUint8Array
    )
    console.log("webverified verified again in nodejs", webVerified)

    const localSigned = await sign.sign(
        pGmessageUint8Array,
        pGprivUint8Array
    )

    const localSignedB64 = Buffer.from(localSigned.signature).toString('base64')
    console.log("localSignedB64")
    console.log(localSignedB64)
    
    const localVerified = await sign.verify(
        localSigned.signature,
        message, 
        pGpubUint8Array
    )
    console.log("Signature in nodejs using public and private keys from Playground", localVerified)
}

run();
